CREATE TABLE IF NOT EXISTS `plugins` ( `id` INT NOT NULL AUTO_INCREMENT , `owner_user_id` TEXT NOT NULL DEFAULT '213655288039866368',`name` TEXT NULL DEFAULT NULL ,`description` TEXT NOT NULL , `creation_date` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_date` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `approved` TINYINT NOT NULL DEFAULT '0' , `awaiting_approval` TINYINT NOT NULL DEFAULT '1' , `banned` TINYINT NOT NULL DEFAULT '0' , `banned_by` TEXT NULL DEFAULT NULL , `banned_date` TIMESTAMP NULL DEFAULT NULL , `version` TEXT NOT NULL DEFAULT '1.0.0-SNAPSHOT' , PRIMARY KEY (`id`));